function testNotif() {
	var regOverdue=0;
	var rightNow = new GlideDateTime();
	var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
	overdueNITasks.addQuery('due_date','<=',rightNow);
	overdueNITasks.addQuery('state','<',3);
	//overdueNITasks.addQuery('assigned_to',current.assigned_to);
gs.info("paso 1");
	overdueNITasks.query();
gs.info("paso 2");
	gs.addInfoMessage("overdueNITasks= "+overdueNITasks);
gs.info("paso 3");
	regOverdue = overdueNITasks.getRowCount();
	regOverdue--;
gs.info("regOverdue = " + regOverdue);
while(overdueNITasks.next()){
	gs.info("4 Overdue NeedIt Task record = " + overdueNITasks.number);
}
	gs.addInfoMessage("In addition to this overdue NeedIt Task, you have " + (overdueNITasks.getRowCount() - 1) + " more overdue NeedIt Tasks.");
gs.info("paso 5");
}
